# live updates are used for client hotfixes or extra functionality

DROP TABLE IF EXISTS liveupdates;
CREATE TABLE liveupdates (
	updateID INT,
	updateName VARCHAR(100),
	description VARCHAR(100),
	machoVersionMin INT,
	machoVersionMax INT,
	buildNumberMin INT,
	buildNumberMax INT,
	methodName VARCHAR(100),
	objectID VARCHAR(100),
	codeType VARCHAR(100),
	code BLOB,
	PRIMARY KEY (updateID)
);

